import { Routes, Route, Link } from "react-router-dom";
import React, { useState, useEffect } from "react";
import footballLogo from '../img/football.png';
import HomePage from './HomePage';
import PlayPage from './PlayPage';
import LoginPage from './LoginPage';
import ProfilePage from './ProfilePage';
import SettingsPage from './SettingsPage';
import ToggleVisability from "../functions/ToggleVisability";

function Menu() {

    // ------------------------------- TESTING THIS OUT
    // const [show, setShow] = useState();

    // function toggleShow() {
    //     setShow(!show);
    // }
    // ------------------------------- TESTING THIS OUT

    const [currentPage, setCurrentPage] = useState("home");

    useEffect(() => {
        if (currentPage === "home") {
            <HomePage />
        } else if (currentPage === "play") {
            <PlayPage />
        } else if (currentPage === "profile") {
            <ProfilePage />
        } else if (currentPage === "login") {
            <LoginPage />
        } else if (currentPage === "settings") {
            <SettingsPage />
        }
    }, [currentPage])

    return(
        <div>
            <div className="header">
                <img className="footballLogo" src={footballLogo} alt="football logo" />
                <h1 className="title">KickBuddy</h1>
                <img className="footballLogo" src={footballLogo} alt="football logo" />
            </div>
            {/* <ToggleVisability> */}
                <div className="container">
                    <div className="play"><Link to="/play"><button onClick={()=>setCurrentPage("play")}>PLAY</button></Link></div>
                    <div className="profile"><Link to="/profile"><button onClick={()=>setCurrentPage("profile")}>PROFILE</button></Link></div>
                    <div className="login"><Link to="/login"><button onClick={()=>setCurrentPage("login")}>LOGIN</button></Link></div>
                    <div className="settings"><Link to="/settings"><button onClick={()=>setCurrentPage("settings")}>SETTINGS</button></Link></div>
                </div>
            {/* </ToggleVisability> */}
            <Routes>
                <Route path="/" element={<HomePage />} />
                <Route path="/play" element={<PlayPage />} />
                <Route path="/login" element={<LoginPage />} />
                <Route path="/profile" element={<ProfilePage />} />
                <Route path="/settings" element={<SettingsPage />} />
                <Route path="*" element={<p>Not Found</p>} />
            </Routes>
        </div>
    );

}

export default Menu;
