import { Routes, Route, Link } from "react-router-dom";
import React, { useState } from "react";
import footballLogo from '../img/football.png';
import homeIcon from '../img/home-icon.png';
import HomePage from './HomePage';
import PlayPage from './PlayPage';
import LoginPage from './LoginPage';
import ProfilePage from './ProfilePage';
import SettingsPage from './SettingsPage';
import ToggleVisability from "../functions/ToggleVisability";

function Menu() {

    // ------------------------------- TESTING THIS OUT
    // const [show, setShow] = useState();

    // function toggleShow() {
    //     setShow(!show);
    // }
    // ------------------------------- TESTING THIS OUT

    return(
        <div>
            <div className="header">
                <img className="footballLogo" src={footballLogo} alt="football logo" />
                <h1 className="title">KickBuddy</h1>
                <img className="footballLogo" src={footballLogo} alt="football logo" />
            </div>
            <ToggleVisability>
                <div className="container">
                    <div className="play"><Link to="/play"><button onClick={<ToggleVisability/>}>PLAY</button></Link></div>
                    <div className="profile"><Link to="/profile"><button>PROFILE</button></Link></div>
                    <div className="login"><Link to="/login"><button>LOGIN</button></Link></div>
                    <div className="settings"><Link to="/settings"><button>SETTINGS</button></Link></div>
                </div>
            </ToggleVisability>
            <Routes>
                <Route path="/" element={<HomePage />} />
                <Route path="/play" element={<PlayPage />} />
                <Route path="/login" element={<LoginPage />} />
                <Route path="/profile" element={<ProfilePage />} />
                <Route path="/settings" element={<SettingsPage />} />
                <Route path="*" element={<p>Not Found</p>} />
            </Routes>
        </div>
    );

}

export default Menu;
