import React, { useState } from "react";

export default function ToggleVisability({children, buttonText}) {

    const [show, setShow] = useState();

    function toggleShow() {
        setShow(!show);
    }

    return(
        <div className="component-container">
            {show && children}
            <button onClick={toggleShow}>{buttonText}</button>
        </div>
    );
}
