import '../../css/ProfilePage.css';
import { useState, useEffect } from "react";
import CircularProgress from '@mui/material/CircularProgress';
import LoginPage from './LoginPage.js';
import ProfileInfo from '../ProfileInfo.js';
import { Typography, Button } from '@mui/material';
import SimpleDialog from '../SimpleDialog';

function ProfilePage({ profileInfo, authenticated, setAuthenticated }) {

    const [profileInfos, setProfileInfos] = useState([]);
    const [loading, setLoading] = useState(true);
    const handleAuthenticated = (isAuthenticated) => {setAuthenticated(isAuthenticated)}

    useEffect(() => {
        fetch('http://unn-w19014367.newnumyspace.co.uk/kickbuddy/api/profile')
            .then(response => response.json())
            .then(
                (json) => {
                    setProfileInfos(json.data);
                    setLoading(false);
                }
            )
            .catch(
                (e) => {
                    console.log(e.message);
                    setLoading(false);
                }
            )
    });

    function FitnessDialog() {
        const [open, setOpen] = useState(false);
        const [selectedValue, setSelectedValue] = useState('');
        
        const handleClickOpen = () => {
            setOpen(true);
        };

        const handleClose = (value) => {
            setOpen(false)
            setSelectedValue(value);
        };

        return(
            <div>
                <Typography variant="subtitle1" component="div">
                    Selected: {selectedValue}
                </Typography>
                <br />
                <Button variant="outlined" onClick={handleClickOpen}>
                    Open Dialog
                </Button>
                <SimpleDialog
                    selectedValue={selectedValue}
                    open = {open}
                    onClose = {handleClose}
                />
            </div>
        );
    }

    function checkUsername() {
        const storedUsername = localStorage.getItem('username');
        const foundUser = profileInfos.find(user => user.username === storedUsername);
        return(foundUser);
    }

    const playerProfile = profileInfos.filter(checkUsername).map(profileInfo => (<ProfileInfo contents={profileInfo} />));

    return(
        <div className="profile-page-container">
            {authenticated && <div> 
                <h2>Profile</h2>
                {loading && <CircularProgress color="inherit" />}
                {playerProfile}
                <div className='grid-container'>
                    <div className='wellbeing-button'>
                        <button>WELLBEING</button>
                    </div>
                    <div className='fitness-button'>
                        <button onClick={FitnessDialog}>FITNESS</button>
                    </div>
                </div>
            </div>    
            }
            {!authenticated && <div>
                <h2>You are not signed in.</h2>
                <LoginPage
                    authenticated={authenticated}
                    setAuthenticated={setAuthenticated}
                    handleAuthenticated={handleAuthenticated}
                />
            </div>   
            }
        </div>
    )

}

export default ProfilePage;
