import { useEffect, useState } from "react";
import CircularProgress from '@mui/material/CircularProgress';
import '../../css/PlayPage.css';
import Match from '../Match.js';
import PostMatch from '../PostMatch.js';
import { Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions, Button } from '@mui/material';


function PlayPage() {

    const [matches, setMatches] = useState([]);
    const [loading, setLoading] = useState(true);
    const [openPostMatch, setOpenPostMatch] = useState(true);

    useEffect( () => {
        fetch('http://unn-w19014367.newnumyspace.co.uk/kickbuddy/api/play')
            .then(response => response.json())
            .then(
                (json) => {
                    setMatches(json.data);
                    setLoading(false);
                }
            )
            .catch (
                (e) => {
                    console.log(e.message);
                    setLoading(false);
                }
            )
    });

    /** Mapping the query result into individual objects */
    const matchesList = matches.slice(1,4).map(match => (<Match key={match.matchID} contents={match} />)); // possibly remove the slice once I figure out page numbers
    
    const postMatch = () => {
        // <PostMatch />   (i think?? Or maybe put <PostMatch /> in onClick function below?)
        alert("Work in Progress! Check back later!");
    }
    
    const extendMatchesList = () => {
        // Code to produce more matches here
        alert("Work in Progress! Check back later!");
    }

    const handleClickToOpenPostMatch = () => {
        setOpenPostMatch(true);
    }
    const handleToClosePostMatch = () => {
        setOpenPostMatch(false);
    }
    
    return(
        <div>
            <div>
                <h2>Find a Match</h2>
                {loading && <CircularProgress color="inherit" />}
            </div>
            <div>
                {matchesList}

                <Dialog open={openPostMatch} onClose={handleToClosePostMatch}>
                    <DialogTitle>{"POST A MATCH"}</DialogTitle>
                    <DialogContent>
                        <DialogContentText>Welcome to the KickBuddy Wellbeing Centre</DialogContentText>
                        <span>Words</span>
                    </DialogContent>
                    <DialogActions>
                        <Button
                            onClick={handleToClosePostMatch}
                            color="primary" 
                            autoFocus>
                                CLOSE
                        </Button>
                    </DialogActions>
                </Dialog>

                <div className="grid-container">
                    <div className="post-match-button">
                        <button onClick={handleClickToOpenPostMatch}>POST MATCH</button>
                    </div>
                    <div className="more-matches-button">
                        <button onClick={extendMatchesList}>MORE MATCHES</button>
                    </div>
                </div>
            </div>
        </div>
    )

}

export default PlayPage;
