import { useState, useEffect } from "react";
import { Buffer } from 'buffer';
import '../../css/LoginPage.css';

function LoginPage({ authenticated, handleAuthenticated }) {

    /** Username and password variables for logging in */
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    /** Login detail variables for signing up to create an account */
    const [signUpName, setSignUpName] = useState("");
    const [signUpUsername, setSignUpUsername] = useState("");
    const [signUpPassword, setSignUpPassword] = useState("");
    const [signUpPassword2, setSignUpPassword2] = useState("");


    useEffect(
        () => {
            if(localStorage.getItem('token')) {
                handleAuthenticated(true);
            }
        }
    )


    /** Arrow functions to handle authentication */
    const handleUsername = (event) => {
        setUsername(event.target.value);
    }
    const handlePassword = (event) => {
        setPassword(event.target.value);
    }
    
    const handleLogin = () => {

        const encodedString = Buffer.from(username + ":" + password).toString('base64');

        fetch("http://unn-w19014367.newnumyspace.co.uk/kf6012/assignment/api/auth",
            {
                method: 'POST',
                headers: new Headers({ "Authorization": "Basic " + encodedString })
            })
        .then(
            (response) => {
                return response.json();
            }
        )
        .then(
            (json) => {
                if(json.message.toLowerCase() === "success")
                    if(json.data.hasOwnProperty('token')) {
                        localStorage.setItem('token', json.data.token);
                        handleAuthenticated(true);
                    }
            }
        )

    }


    /** Variables to store new user details to add to database */
    
    
    return(
        <div>
            {authenticated && <div>
                <h2>Sign Out</h2>
                <input type="button" value="Sign out" onClick={handleSignOut} />
            </div>
            }
            {!authenticated && <div>
                
            </div>
            }
            <h2>Sign In</h2>
            <div className="login-container">
                <div className="login-container-login">
                    <p>Sign in with your username and password</p>
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={handleUsername}
                    />
                    <br/>
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={handlePassword}
                    />
                    <br/>
                    <input
                        type="button"
                        value="Sign In"
                        onClick={handleLogin}
                    />
                </div>
                <br />
                <div className="login-container-signup">
                    <p>Don't have an account? Sign up here:</p>
                    <input
                        type="text"
                        placeholder="Name"
                        value={signUpName}
                        // need to add onChange={handleSignUpName}
                    />
                    <br/>
                    <input
                        type="text"
                        placeholder="Username"
                        value={signUpUsername}
                    />
                    <br/>
                    <input
                        type="password"
                        placeholder="Password"
                        value={signUpPassword}
                    />
                    <br/>
                    <input
                        type="password"
                        placeholder="Password"
                        value={signUpPassword2}
                    />
                    <input
                        type="button"
                        value="Sign Up"
                        // need to write handleSignUp function to add user details to database
                        // also need validation for that info
                    />
                </div>
            </div>
        </div>
    )
    
}

export default LoginPage;
