import React, { useState } from "react";
import homeIcon from '../img/home-icon.png';

export default function ToggleVisability({children}) {

    const [show, setShow] = useState(true);

    function toggleShow() {
        setShow(!show);
    }

    return(
        <div className="component-container">
            {show && children}
            <button className="home-icon-button" onClick={toggleShow}><img className="home-icon" src={homeIcon} alt="home icon" /></button>
        </div>
    );
}
