import '../../css/SettingsPage.css';
import Switch from '@mui/material/Switch';
import changeBackgroundIcon from "../../img/background-change-icon.png";
import { useState } from "react";

function SettingsPage() {

    const [darkMode, setDarkMode] = useState(false);

    const handleDarkModeChange = () => {
        setDarkMode(!darkMode);
        if(darkMode) {
            document.getElementsByClassName('App').style.backgroundImage = "url('../../img/background-pitch-dark.png')";
        }
    }

    const changeBackground = () => {
        // code here to change background
        alert("You have changed the background!"); //remove after testing
    }
    
    return(
        
        <div>
            <h2>Settings</h2>
            <div className="settings-container">
                <div>
                    <p>
                        Dark Mode
                        <Switch 
                            className="dark-mode-switch" 
                            onChange={handleDarkModeChange} 
                            color="default"
                        />
                    </p>
                </div>
                <div>
                    <p>
                        Change background
                        <button className="background-button" onClick={changeBackground}>
                            <img className="background-change-icon" src={changeBackgroundIcon} alt="Change background icon"/>
                        </button>
                    </p>

                </div>
            </div>
        </div>
    )

}

export default SettingsPage;
