import { useEffect, useState } from "react";
import CircularProgress from '@mui/material/CircularProgress';
import '../../css/PlayPage.css';
import Match from '../Match.js';
import { Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions, Button } from '@mui/material';


function PlayPage() {

    const [matches, setMatches] = useState([]);
    const [loading, setLoading] = useState(true);
    const [openPostMatch, setOpenPostMatch] = useState(false);

    /** Variables to store uploaded match details */
    const [upload_Team1, setUpload_Team1] = useState("");
    const [upload_Team2, setUpload_Team2] = useState("");
    const [upload_Date, setUpload_Date] = useState("");
    const [upload_Time, setUpload_Time] = useState("");
    const [upload_Venue, setUpload_Venue] = useState("");

    useEffect( () => {
        fetch('http://unn-w19014367.newnumyspace.co.uk/kickbuddy/api/play')
            .then(response => response.json())
            .then(
                (json) => {
                    setMatches(json.data);
                    setLoading(false);
                }
            )
            .catch (
                (e) => {
                    console.log(e.message);
                    setLoading(false);
                }
            )
    });

    /** Mapping the query result into individual objects */
    const matchesList = matches.slice(1,4).map(match => (<Match key={match.matchID} contents={match} />)); // possibly remove the slice once I figure out page numbers
    
    const extendMatchesList = () => {
        // Code to produce more matches here
        alert("Work in Progress! Check back later!");
    };

    const handleClickToOpenPostMatch = () => {
        setOpenPostMatch(true);
    };
    const handleToClosePostMatch = () => {
        setOpenPostMatch(false);
    };

    const handlePostMatch = () => {
        setUpload_Team1(upload_Team1)
        setUpload_Team1(upload_Team2)        
        setUpload_Team1(upload_Date)
        setUpload_Team1(upload_Time)
        setUpload_Team1(upload_Venue)
        console.log(upload_Team1);
    };
    
    return(
        <div>
            <div>
                <h2>Find a Match</h2>
                {loading && <CircularProgress color="inherit" />}
            </div>
            <div>
                {matchesList}

                <Dialog open={openPostMatch} onClose={handleToClosePostMatch}>
                    <DialogTitle>{"POST A MATCH"}</DialogTitle>
                    <DialogContent>
                        <DialogContentText>Upload a match</DialogContentText>
                        <div>
                            <input 
                                type="text"
                                placeholder="Enter Team 1"
                                defaultValue={upload_Team1}
                            />
                            <input 
                                type="text"
                                placeholder="Enter Team 2"
                                value={upload_Team2}
                            />
                            <input 
                                type="text"
                                placeholder="Enter date (dd/mm/yy)"
                                value={upload_Date}
                            />
                            <input 
                                type="text"
                                placeholder="Enter time (hh:mm)"
                                value={upload_Time}
                            />
                            <input 
                                type="text"
                                placeholder="Enter Venue"
                                value={upload_Venue}
                            />
                            <button onClick={handlePostMatch}>POST</button>
                        </div>
                    </DialogContent>
                    <DialogActions>
                        <Button
                            onClick={handleToClosePostMatch}
                            color="primary" 
                            autoFocus>
                                CLOSE
                        </Button>
                    </DialogActions>
                </Dialog>

                <div className="grid-container">
                    <div className="post-match-button">
                        <button onClick={handleClickToOpenPostMatch}>POST MATCH</button>
                    </div>
                    <div className="more-matches-button">
                        <button onClick={extendMatchesList}>MORE MATCHES</button>
                    </div>
                </div>
            </div>
        </div>
    )

}

export default PlayPage;
