import { useEffect, useState } from "react";
import CircularProgress from '@mui/material/CircularProgress';
import '../../css/PlayPage.css';
import Match from '../Match.js';
import PostMatch from '../PostMatch.js';


function PlayPage() {

    const [matches, setMatches] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect( () => {
        fetch('http://unn-w19014367.newnumyspace.co.uk/kickbuddy/api/play')
            .then(response => response.json())
            .then(
                (json) => {
                    setMatches(json.data);
                    setLoading(false);
                }
            )
            .catch (
                (e) => {
                    console.log(e.message);
                    setLoading(false);
                }
            )
    });

    /** Mapping the query result into individual objects */
    const matchesList = matches.slice(1,4).map(match => (<Match key={match.matchID} contents={match} />)); // possibly remove the slice once I figure out page numbers

    const extendMatchesList = () => {
        // Code to produce more matches here
        alert("Work in Progress! Check back later!");
    }
    
    return(
        <div>
            <div>
                <h2>Find a Match</h2>
                {loading && <CircularProgress color="inherit" />}
            </div>
            <div>
                {matchesList}
                <div className="grid-container">
                    <div className="post-match-button">
                        <button /*onClick={<PostMatch/>}*/>POST MATCH</button>
                    </div>
                    <div className="more-matches-button">
                        <button onClick={extendMatchesList}>MORE MATCHES</button>
                    </div>
                </div>
            </div>
        </div>
    )

}

export default PlayPage;
