import { Routes, Route, Link } from "react-router-dom";
import React, { useState } from "react";
import footballLogo from '../img/football.png';
import homeIcon from '../img/home-icon.png';
import HomePage from './pages/HomePage';
import PlayPage from './pages/PlayPage';
import LoginPage from './pages/LoginPage';
import ProfilePage from './pages/ProfilePage';
import SettingsPage from './pages/SettingsPage';
import ToggleVisability from "../functions/ToggleVisability";

function Menu() {

    /** Code to hide/unhide the menu by clicking the Home button/Other buttons */
    const [show, setShow] = useState(true);
    const [profileInfo, setProfileInfo] = useState([]);
    const handleAuthenticated = (isAuthenticated) => {setAuthenticated(isAuthenticated)}
    const [authenticated, setAuthenticated] = useState("");

    function toggleShow() {
        setShow(!show);
    }
    function toggleShowHomeButton() {
        if (!show) {
            setShow(!show);
        }
        else { setShow(show) }
    }

    const menu = <div className="container">
                    <div className="play"><Link to="/play"><button onClick={toggleShow}>PLAY</button></Link></div>
                    <div className="profile"><Link to="/profile"><button onClick={toggleShow}>PROFILE</button></Link></div>
                    <div className="login"><Link to="/login"><button onClick={toggleShow}>LOGIN</button></Link></div>
                    <div className="settings"><Link to="/settings"><button onClick={toggleShow}>SETTINGS</button></Link></div>
                </div>
    /** End of code to hide/unhide the menu by clicking the Home button */

    return(
        <div>
            <div className="header">
                <div><Link to="/"><button className="home-icon-button" onClick={toggleShowHomeButton}><img className="home-icon" src={homeIcon} alt="home icon" /></button></Link></div>
                <img className="football-logo" src={footballLogo} alt="football logo" />
                <h1 className="title">KickBuddy</h1>
                <img className="football-logo" src={footballLogo} alt="football logo" />
            </div>
                {show && menu}
            <Routes>
                <Route path="/" element={<HomePage />} />
                <Route path="/play" element={<PlayPage />} />
                <Route
                    path="/login"
                    element={
                        <LoginPage
                            authenticated={authenticated}
                            handleAuthenticated={handleAuthenticated}
                        />
                    } 
                 />
                <Route path="/profile" element={<ProfilePage profileInfo={profileInfo} />} />
                <Route path="/settings" element={<SettingsPage />} />
                <Route path="*" element={<p>Not Found</p>} />
            </Routes>
        </div>
    );

}

export default Menu;
