import React, { useState } from "react";

export default function ToggleVisability({children}) {

    const [show, setShow] = useState();

    function toggleShow() {
        setShow(!show);
    }

    return(
        <div className="component-container">
            {show && children}
            {/* <button onClick={toggleShow}>TESTBUTTON</button> */}
        </div>
    );
}
