import '../../css/ProfilePage.css';
import { useState, useEffect } from "react";
import ProfileInfo from '../ProfileInfo.js';

function ProfilePage({ profileInfo, authenticated, handleAuthenticated }) {

    const [profileInfos, setProfileInfos] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetch('http://unn-w19014367.newnumyspace.co.uk/kickbuddy/api/profile')
            .then(response => response.json())
            .then(
                (json) => {
                    setProfileInfos(json.data);
                    setLoading(false);
                }
            )
            .catch(
                (e) => {
                    console.log(e.message);
                    setLoading(false);
                }
            )
    });

    const playerProfile = profileInfos.map(profileInfo => (<ProfileInfo contents={profileInfo} />));

    return(
        <div>
            {authenticated && <div>
                <h2>Profile</h2>
                {loading && <p>Loading...</p>}
                {playerProfile}
            </div>    
            }
            {!authenticated && <div>
                <h2>You are not signed in.</h2>
            </div>   
            }
        </div>
    )

}

export default ProfilePage;
