import { useState, useEffect } from "react";
import '../../css/LoginPage.css';

function LoginPage() {

    /** Username and password variables for logging in */
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    /** Login detail variables for signing up to create an account */
    const [signUpName, setSignUpName] = useState("");
    const [signUpUsername, setSignUpUsername] = useState("");
    const [signUpPassword, setSignUpPassword] = useState("");
    const [signUpPassword2, setSignUpPassword2] = useState("");

    /** Arrow functions to handle authentication */
    const handleUsername = (event) => {
        setUsername(event.target.value);
    }
    const handlePassword = (event) => {
        setPassword(event.target.value);
    }


    useEffect(() => {

    });

    /** Variables to store new user details to add to database */
    
    
    return(
        <div>
            <h1>Sign In</h1>
            <div className="login-container">
                <div className="login-container-login">
                    <p>Sign in with your username and password</p>
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={handleUsername}
                    />
                    <br/>
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={handlePassword}
                    />
                    <br/>
                    <input
                        type="button"
                        value="Sign In"
                        //need to write handleLogin function and add onClick={handleLogin}
                    />
                </div>
                <br />
                <div className="login-container-signup">
                    <p>Don't have an account? Sign up here:</p>
                    <input
                        type="text"
                        placeholder="Name"
                        value={signUpName}
                        // need to add onChange={handleSignUpName}
                    />
                    <br/>
                    <input
                        type="text"
                        placeholder="Username"
                        value={signUpUsername}
                    />
                    <br/>
                    <input
                        type="password"
                        placeholder="Password"
                        value={signUpPassword}
                    />
                    <br/>
                    <input
                        type="password"
                        placeholder="Password"
                        value={signUpPassword2}
                    />
                    <input
                        type="button"
                        value="Sign Up"
                        // need to write handleSignUp function to add user details to database
                        // also need validation for that info
                    />
                </div>
            </div>
        </div>
    )
    
}

export default LoginPage;
